mapper.insert:
INSERT INTO process.if_info_logs
(
    if_id
    , if_request_at
    , if_req_json
    , if_tr_id
) VALUES (
    #{ifId}
    , #{ifRequestAt}
    , #{ifReqJson}
    , #{ifTrId}
)

mapper.update:
UPDATE process.if_info_logs
if_response_at = #{ifResponseAt}, if_step1_at = #{ifStep1At}, if_step2_at = #{ifStep2At}, if_step3_at = #{ifStep3At}, if_res_json = #{ifResJson}, if_err_flag = #{ifErrFlag}, if_err_msg = #{ifErrMsg}, last_mod_at = now()
WHERE seq_no = #{seqNo}
