mapper.select:
select pac.loyaltyexpirationdate__c as cnhExpirationDate , pac.row_id as contactId , pac.firstname as firstName , pac.lastname as lastName , pac.personbirthdate as birthDate , pac.gender__pc as gender , pac.personhomephone as homePhone , pac.companyphone__pc as workPhone , pac.personmobilephone as cellPhone , pac.personemail as emailAddress , pac.amount_friends as amountFriends , pac.amount_children as amountChildren , case when pac.optinflag__c = true then 'Y' else 'N' end as loyaltyOPTINFla , pac.loyaltyactivationdate__c as loyaltyActivationDate , pac.loyaltyexpirationtodate__c as loyaltyExpirationDate , pac.loyaltyengagementindex__c as loyaltyEngagementIndex , pac.satisficationlevel__c as loyaltySatisfactionLevel , pac.loyaltylifecycle__c as loyaltyLifeCycle , pac.crmindex__c as loyaltyCRMIndex , pac.satisficationlevel__c as loyaltyTierLevel , pac.loyaltystatus__c as loyaltyStatus , pac.billingstreet as streetAddress , pac.billingstreet_2__c as streetAddress2 , pac.billingstreet_3__c as streetAddress3 , pac.billingcountry as county , pac.billingcity as city , pac.billingstate as state , pac.billingpostalcode as postalCode , pac.numeric_row_id as numericRowId , case when pac.emailoptyn__pc = true then 'Y' else 'N' end suppressAllEmails , case when pac.calloptyn__pc = true then 'Y' else 'N' end SuppressAllCalls , case when pac.directmailoptyn__pc = true then 'Y' else 'N' end SuppressAllMailings , case when pac.x_printed_card__c = true then 'Y' else 'N' end SuppressAllVideoCall , case when pac.blockedwhatsapp__c = true then 'Y' else 'N' end SuppressAllWhatsapp , case when pac.blockedmobile__c = true then 'Y' else 'N' end SuppressAllMobile , pac.blockedsms__c as suppressAllSMS , case when pac.x_printed_card__c = true then 'Y' else 'N' end printedCard from process.account pac where 1=1
and pac.row_id = #{contactId}
and pac.cpf__c = #{cpf}
