mapper.select:
select sr.contact_row_id__c as rowid , sr.protocol__c as protocol , sr.status as status from process.service_request sr join process.opportunity po on sr.protocol__c = po.protocol__c join process.account pa on po.dealercode__c = pa.dealercode__c where po.qexperttransferreddate__c <= (now() - '5 minutes'::interval) and po."type" != 'Test Drive' and po."type" != 'Showroom'
and sr.status = #{statusLead}
and pa.dealercode__c = #{dealerCode}
and pa.apikey__c = #{apiKey}
and po.qexperttransferreddate__c >= to_timestamp(#{date}||' 00:00:00','YYYY-MM-DD HH24:mi:SS') and po.qexperttransferreddate__c <= to_timestamp(#{date}||' 23:59:59', 'YYYY-MM-DD HH24:mi:SS')

select sr.protocol__c as protocolo , sr.business_row_id as codigoConcessionaria , sr.origin as origem , sr.method as metodo , (sr.legacy_created - interval '3 hours') as dataAbertura , pc.firstname||' '||pc.lastname as nomeResponsavel , sr.status as status , 'high' as prioridade , pc.pcd__c as identificacaoPCD , pc.type as tipoContato , 'Y' as clienteHyundai , sr.usedcar_as_down_payment__c::varchar as usadoNaTroca , pc.corporaterepresentativename__c as nomeConcessionaria , pc.cust_stat_cd__c as statusConcessionaria , sr.method as metodoContato , sr.description as observacoesContato , sr.x_camp_id__c as dadosCampanha , sr.source_tag__c as sourceTag , sr.medium_tag__c as mediumTag , sr.content_tag__c as contentTag , sr.form__c as form from process.service_request sr join process.account pc on sr.business_row_id = pc.dealercode__c where sr.protocol__c = #{protocol}
and pc.apikey__c = #{apiKey}
limit 1
