        , SOURCE_CD
        , MAIN_PH_NUM
        , MAIN_FAX_PH_NUM
        , MAIN_EMAIL_ADDR
        , URL
    )
values
    (
        now()
        , 'interface'
        , now()
        , 'interface'
        , #{integrationId}
        , #{cnpjNumber}
        , #{name}
        , #{nickname}
        , #{parentAccountName}
        , #{agencyCode}
        , to_date(#{contractedSince},'YYYY-MM-DD')
        , #{source}
        , #{mainPhoneNumber}
        , #{mainFaxNumber}
        , #{mainEmailAddress}
        , #{homepage}
    );

